import React, { useState, useEffect } from "react";
import axios from "axios";

function BookInfo(props) {

  const [bookName, setBookName] = useState("");
  const [author, setAuthor] = useState("");
  const [bookDes, setBookDes] = useState("");
  const [genre, setGenre] = useState("");
  const [imageBuffer, setImageBuffer] = useState([]);
  const [imageType, setImageType] = useState("");

  const renderImage = (imageFile) => {

    const contentType = imageFile.type;
    const arrayBuffer = imageFile.buffer;

    const binary = Buffer.from(arrayBuffer).toString('base64'); // Convert Buffer data to Binary

    const base64Flag = `data:${contentType};base64,`; // Set to the right format: data:[data-type];base64,[binary]

    return base64Flag + binary
  }

  useEffect(() => {

    axios.get("http://localhost:4000/view-book/" + props.match.params.id).then(res => {
      console.log(res.data.image.data.data);
      setBookName(res.data.name);
      setAuthor(res.data.author);
      setBookDes(res.data.description);
      setBookDes(res.data.genre);
      setImageBuffer(res.data.image.data.data);
      setImage({buffer: res.data.image.data.data, type: res.data.image.contentType});
    }).catch(error => {
      console.log(error);
    });

  }, [props.match.params.id]);

  return (
    <div>
      <h1>{bookName}</h1>
      <p>{author}</p>

    </div>
  )
}

export default BookInfo;
