import React from "react";
import Card from "react-bootstrap/Card";
import Col from "react-bootstrap/Col";

function BookCard(props) {

  const renderImage = (imageFile) => {

    const contentType = imageFile.contentType;
    const arrayBuffer = imageFile.data.data;

    const binary = Buffer.from(arrayBuffer).toString('base64');

    const base64Flag = `data:${contentType};base64,`;

    return base64Flag + binary
  }

  return (
    <div>
      <Card variant="border">
        <Card.Img className="book" variant="book" src={renderImage(props.image)} alt="book-image" />
        <Card.Body>
          <Card.Title>{props.name}</Card.Title>
        </Card.Body>
      </Card>
    </div>
  )
}

export default BookCard;
