import React from "react";
import Card from "react-bootstrap/Card";
import Col from "react-bootstrap/Col";

function BookCard(props) {

  const renderImage = (imageFile) => {

    const contentType = imageFile.contentType;
    const arrayBuffer = imageFile.data.data;

    const binary = Buffer.from(arrayBuffer).toString('base64');

    const base64Flag = `data:${contentType};base64,`;

    return base64Flag + binary
  }

  return (
    <div>
      <Col md="4" sm="6">
        <Card className="card">
          <Card.Img className="card-image" src={renderImage(props.image)} alt="book-image" />
          <Card.Body>
            <Card.Title className="card-title">{props.name}</Card.Title>
            <Card.Subtitle className="mb-2 text-muted">By {props.author}</Card.Subtitle>
            <Card.Subtitle className="mb-2 text-muted">{props.genre}</Card.Subtitle>
          </Card.Body>
        </Card>
      </Col>
    </div>
  )
}

export default BookCard;
