import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

function AddBook() {

  const [image, setImage] = useState({});

  const uploadImage = (event) => {
    if (event.target.files.length === 0) {
      
    }
    setImage({
      file: URL.createObjectURL(event.target.files[0])
    })
  }

  return (
    <div>
      <div className="preview-image">
        {image.file ? <img src={image.file}/> : null}
      </div>
      <Form>

        <Form.Group controlId="book-name">
          <Form.Label>Name</Form.Label>
          <Form.Control type="text" placeholder="An Awesome Book!"/>
        </Form.Group>

        <Form.Group controlId="book-author">
          <Form.Label>Author</Form.Label>
          <Form.Control type="text" placeholder="John Doe"/>
        </Form.Group>

        <Form.Group controlId="book-summary">
          <Form.Label>Description</Form.Label>
          <Form.Control style={{resize: "none"}} as="textarea" rows="5" placeholder="This is a book about awesome books!"/>
        </Form.Group>

        <Form.Group controlId="book-genre">
          <Form.Label>Genre</Form.Label>
          <Form.Control as="select">
            <option>Choose...</option>
            <option>Fiction</option>
            <option>Action</option>
          </Form.Control>
        </Form.Group>

        <Form.Group>
          <Form.Label>Book Cover</Form.Label>
          <Form.Control type="file" accept="image/*" onChange={uploadImage}/>
        </Form.Group>

      </Form>
    </div>
  )
}

export default AddBook;
