import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";

function AddBook() {

  const [bookName, setBookName] = useState("");
  const [author, setAuthor] = useState("");
  const [bookDes, setBookDes] = useState("");
  const [image, setImage] = useState({});

  const uploadImage = (event) => {
    if (event.target.files.length === 0) { // When image upload is cancelled
      setImage({});
    } else {
      setImage({
        file: URL.createObjectURL(event.target.files[0])
      })
    }
  }

  const changeBookName = (event) => {
    setBookName(event.target.value);
  }

  const changeAuthor = (event) => {
    setAuthor(event.target.value);
  }

  const changeBookDes = (event) => {
    setBookDes(event.target.value);
  }

  const genreSelect = (event) => {
    
  }

  return (
    <div className="form-wrapper">
      <div className="preview-image-wrapper">
        <img src={image.file ? image.file : "https://via.placeholder.com/400?text=Book+Photo"}/>
      </div>

      <Form>
        <Form.Row>
          <Form.Group className="form-input" as={Col} controlId="book-name">
            <Form.Label>Name</Form.Label>
            <Form.Control type="text" value={bookName} placeholder="An Awesome Book!" onChange={changeBookName}/>
          </Form.Group>

          <Form.Group className="form-input" as={Col} controlId="book-author">
            <Form.Label>Author</Form.Label>
            <Form.Control type="text" value={author} placeholder="John Doe" onChange={changeAuthor}/>
          </Form.Group>
        </Form.Row>

        <Form.Group className="form-input" controlId="book-summary">
          <Form.Label>Description</Form.Label>
          <Form.Control style={{resize: "none"}} as="textarea" rows="5" value={bookDes} placeholder="This is a book about awesome books!" onChange={changeBookDes}/>
        </Form.Group>

        <Form.Row>

          <Form.Group className="form-input" as={Col} controlId="book-genre">
            <Form.Label>Genre</Form.Label>
            <Form.Control as="select" onChange={genreSelect}>
              <option>Choose...</option>
              <option>Fiction</option>
              <option>Action</option>
              <option>Thriller</option>
              <option>Business</option>
            </Form.Control>
          </Form.Group>

          <Form.Group className="form-input" as={Col} controlId="book-image">
            <Form.Label>Photo</Form.Label>
            <Form.Control type="file" accept="image/*" onChange={uploadImage}/>
          </Form.Group>

        </Form.Row>

      </Form>
    </div>
  )
}

export default AddBook;
