import React from "react";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";

function Search(props) {
  return(
    <div>
      <Form.Row>

        <Form.Group as={Col}>
          <Form.Control type="text" placeholder="Search" value={props.search} onChange={(event) => props.setSearch(event.target.value)} />
        </Form.Group>

        <Form.Group as={Col}>
          <Form.Control as="select">
            <option>Search by...</option>
            <option>Title</option>
            <option>Author name</option>
          </Form.Control>
        </Form.Group>

      </Form.Row>
    </div>
  )
}

export default Search;
