import React from "react";
import Card from "react-bootstrap/Card";
import Col from "react-bootstrap/Col";

function BookCard(props) {

  const renderImage = (imageFile) => {

    const contentType = imageFile.contentType;
    const arrayBuffer = imageFile.data.data;

    const binary = Buffer.from(arrayBuffer).toString('base64')

    const base64Flag = `data:${contentType};base64,${binary}`;

    return base64Flag
  }

  return (
    <div>
      <Col md={4}>
        <Card>
          <img src={renderImage(props.image)} alt="Red dot" />
        </Card>
      </Col>
      <p>{props.name}</p>
    </div>
  )
}

export default BookCard;
