import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import CustomCarousel from "./CustomCarousel";

function AddBook() {

  fileObj = [];
  fileArray = [];

  const [images, setImages] = useState([]);

  const uploadImages = (event) => {
    if (event.target.files.length === 0) { // When image upload is cancelled
      setImage([]);
    } else {
      fileObj.pus(event.target.files)
      for (let i = 0; i < fileObj[0].length; i++) {
        fileArray.push(URL.createObjectURL(fileObj[0][i]))
      }

      setImage([])
    }
  }

  return (
    <div className="form-wrapper">
      <div className="preview-image-wrapper">
        <img src={image.file ? image.file : "https://via.placeholder.com/200?text=Book+Photo"}/>
      </div>

      <Form>
        <Form.Row>
          <Form.Group className="form-input" as={Col} controlId="book-name">
            <Form.Label>Name</Form.Label>
            <Form.Control type="text" placeholder="An Awesome Book!"/>
          </Form.Group>

          <Form.Group className="form-input" as={Col} controlId="book-author">
            <Form.Label>Author</Form.Label>
            <Form.Control type="text" placeholder="John Doe"/>
          </Form.Group>
        </Form.Row>

        <Form.Group className="form-input" controlId="book-summary">
          <Form.Label>Description</Form.Label>
          <Form.Control style={{resize: "none"}} as="textarea" rows="5" placeholder="This is a book about awesome books!"/>
        </Form.Group>

        <Form.Row>

          <Form.Group className="form-input" as={Col} controlId="book-genre">
            <Form.Label>Genre</Form.Label>
            <Form.Control as="select">
              <option>Choose...</option>
              <option>Fiction</option>
              <option>Action</option>
              <option>Thriller</option>
              <option>Business</option>
            </Form.Control>
          </Form.Group>

          <Form.Group className="form-input" as={Col} controlId="book-image">
            <Form.Label>Images</Form.Label>
            <Form.Control type="file" accept="image/*" onChange={uploadImages}/>
          </Form.Group>

        </Form.Row>

      </Form>
    </div>
  )
}

export default AddBook;
