import React from "react";
import Card from "react-bootstrap/Card";
import Col from "react-bootstrap/Col";

function BookCard(props) {

  const renderImage = (imageFile) => {
    const binary = "";
    const contentType = imageFile.contentType;
    const arrayBuffer = imageFile.data.data;

    const bytes = [].slice.call(new Uint8Array(arrayBuffer));
    bytes.forEach((byte) => binary += String.fromCharCode(byte));

    binary = window.btoa(binary);

  }

  return (
    <div>
      <Col md={4}>
        <Card.Img variant="top" src={renderImage(props.image)} />
      </Col>
    </div>
  )
}

export default BookCard;
